mapper.insert:

	SELECT BOARD_SEQ.nextval
	FROM DUAL

INSERT INTO BOARD(
					BOARD_NO
					,WRITER
					,TITLE
					,CONTENT
					,BOARD_TYPE
					,REGDATE
					,VIEW_COUNT
					)
			VALUES(
					#{boardNo}
					,#{writer}
					,#{title}
					,#{contentBlob}
					,'N'
					,sysdate
					,0
					)

mapper.update:
UPDATE BOARD
SET TITLE=#{title}
	,CONTENT=#{contentBlob}
	,UPDATE_DATE=TO_DATE(sysdate)
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET VIEW_COUNT = VIEW_COUNT+1
WHERE BOARD_NO = #{boardNo}
